          function init(container) {
            if (!container || container.dataset.afaqBound === "1") return;
            var video = container.querySelector('.afaq-video-el');
            var btn = container.querySelector('.afaq-video-toggle');
            if (!video || !btn) return;

            function sync() {
              if (video.paused) {
                container.classList.remove('is-playing');
                btn.setAttribute('aria-pressed', 'false');
                btn.setAttribute('aria-label', 'Play video');
              } else {
                container.classList.add('is-playing');
                btn.setAttribute('aria-pressed', 'true');
                btn.setAttribute('aria-label', 'Pause video');
              }
            }

            btn.addEventListener('click', function () {
              // Try play; some browsers return a promise
              if (video.paused) {
                var p = video.play();
                if (p && typeof p.catch === 'function') {
                  p.catch(function (err) { console && console.warn && console.warn('Play failed:', err); });
                }
              } else {
                video.pause();
              }
            });

            // Allow clicking the video itself to toggle
            video.addEventListener('click', function () {
              if (video.paused) { video.play(); } else { video.pause(); }
            });

            // Keep UI in sync
            video.addEventListener('play', sync);
            video.addEventListener('pause', sync);
            video.addEventListener('ended', sync);

            sync();
            container.dataset.afaqBound = "1";
          }

